script:
frappe.ui.form.on("Asset Maintenance Request", {
    setup: function(frm) {
        frm.set_query("assert", function() {
            return {
                filters: {
                    status: "In Use"
                }
            };
        });
    }
});

frappe.ui.form.on('Task', {
    before_save: function(frm) {
        // Auto-fill subject if it's empty
        if (!frm.doc.subject) {
            let subject_text = "Asset Task";

            if (frm.doc.asset) {
                subject_text = `Maintenance for ${frm.doc.asset}`;
            } else if (frm.doc.asset_maintenance_request) {
                subject_text = `Maintenance Request ${frm.doc.asset_maintenance_request}`;
            }

            frm.set_value('subject', subject_text);
        }
    }
});

frappe.ui.form.on("Asset Maintenance Request", {
    refresh: function(frm) {
        if (!frm.is_new() && frappe.user.has_role("Maintenance Team Supervisor")) {
            frm.add_custom_button(__('Create Maintenance Task'), function() {
                frappe.call({
                    method: "frappe.client.insert",
                    args: {
                        doc: {
                            doctype: "Task",
                            subject: `${frm.doc.asset_name || ''} - ${frm.doc.maintenance_type || ''} - ${frm.doc.name}`,
                            asset_maintenance_request: frm.doc.name,
                            asset: frm.doc.asset,
                            maintenance_type: frm.doc.maintenance_type,
                            expected_completion_date: frm.doc.expected_completion_date,
                            priority: frm.doc.priority,
                            status: "Open"
                        }
                    },
                    callback: function(r) {
                        if (!r.exc) {
                            frappe.msgprint(`✅ Task <b>${r.message.name}</b> created successfully.`);
                            frm.set_value("status", "In Progress");
                            frm.save();
                        }
                    }
                });
            }).addClass('btn-primary');
        }
    }
});
